var fileJSON=[{"path":"/home/angelica/exm_MX/hotspot/hotspot_common.h", "name":"hotspot_common.h", "has_active_debug_locs":false, "absName":"/home/angelica/exm_MX/hotspot/hotspot_common.h", "content":"#define AMB_TEMP (80.0f)\012\012// Block size\012#ifndef BSIZE\012	#ifdef AMD\012		#define BSIZE 16\012	#else\012		#define BSIZE 512\012	#endif\012#endif\012\012#ifndef BLOCK_X\012	#define BLOCK_X 16\012#endif\012\012#ifndef BLOCK_Y\012	#define BLOCK_Y 16\012#endif\012\012// Vector size\012#ifndef SSIZE\012	#ifdef AOCL_BOARD_de5net_a7\012		#define SSIZE 8\012	#elif AOCL_BOARD_p385a_sch_ax115\012		#define SSIZE 8\012	#elif AMD\012		#define SSIZE 4\012	#else\012		#define SSIZE 64\012	#endif\012#endif\012\012// Radius of stencil, e.g 5-point stencil => 1\012#ifndef RAD\012  #define RAD  1\012#endif\012\012// Number of parallel time steps\012#ifndef TIME\012	#ifdef AOCL_BOARD_de5net_a7\012		#define TIME 6\012	#elif AOCL_BOARD_p385a_sch_ax115\012		#define TIME 21\012	#elif AMD\012		#define TIME 1\012	#else\012		#define TIME 6\012	#endif\012#endif\012\012// Padding to fix alignment for time steps that are not a multiple of 8\012#ifndef PAD\012  #define PAD TIME % 16\012#endif\012\012#define HALO_SIZE		TIME * RAD			// halo size\012#define BACK_OFF		2 * HALO_SIZE			// back off for going to next block\012"}, {"path":"/home/angelica/exm_MX/hotspot/hotspot_kernel_v5_MX.cl", "name":"hotspot_kernel_v5_MX.cl", "has_active_debug_locs":false, "absName":"/home/angelica/exm_MX/hotspot/hotspot_kernel_v5_MX.cl", "content":"#include \"hotspot_common.h\"\012\012// input shift register parameters\012#define IN_SR_BASE		2 * BSIZE				// this shows the point to write into the shift register; one row for top neighbors and one row for center\012#define IN_SR_SIZE		IN_SR_BASE + SSIZE		// SSIZE indexes are enough for the bottommost row\012\012// offsets for reading from the input shift register\012#define SR_OFF_C		BSIZE				// center\012#define SR_OFF_N		SR_OFF_C + BSIZE		// north  (+y direction)\012#define SR_OFF_S		SR_OFF_C - BSIZE		// south  (-y direction)\012#define SR_OFF_E		SR_OFF_C + 1			// east   (-x direction)\012#define SR_OFF_W		SR_OFF_C - 1			// west   (+x direction)\012\012__attribute__((max_global_work_dim(0)))\012__kernel void hotspot(__global __attribute((buffer_location(\"HBM1\"))) const float* restrict pwr_in,		// power input\012                      __global __attribute((buffer_location(\"HBM2\"))) const float* restrict src,			// temperature input\012                      __global __attribute((buffer_location(\"HBM3\")))      float* restrict dst,			// temperature output\012                               const int             grid_cols,		// number of columns\012                               const int             grid_rows,		// number of rows\012                               const float           sdc,			// step/Capacitance\012                               const float           Rx_1,\012                               const float           Ry_1,\012                               const float           Rz_1,\012                               const int             comp_exit)		// exit condition for compute loop\012{\012	float in_sr[IN_SR_SIZE];									// shift register for spatial blocking\012\012	// initialize the shift register\012	#pragma unroll\012	for (int i = 0; i < IN_SR_SIZE; i++)\012	{\012		in_sr[i] = 0.0f;\012	}\012\012	// starting points\012	int x = 0;\012	int y = 0;\012	int bx = 0;											// block number in x dimension\012	int index = 0;											// global index\012\012	while (index != comp_exit)\012	{\012		index++;											// increment global index\012		\012		// shift shift register by SSIZE\012		#pragma unroll\012		for (int i = 0; i < IN_SR_BASE; i++)\012		{\012			in_sr[i] = in_sr[i + SSIZE];\012		}\012\012		#pragma unroll\012		for (int i = 0; i < SSIZE; i++)\012		{\012			int gx = bx + x - 1;							// global x position offset\012			int real_x = gx + i;							// global x position\012			int comp_offset_y = y - 1;						// global y position, will be out-of-bound for first and last iterations\012			int comp_offset = gx + comp_offset_y * grid_cols;		// index offset for computation, reading from power buffer, and writing back to destination\012			int read_offset = comp_offset + grid_cols;			// index to read from memory\012			int real_block_x = x + i - 1;						// local x position in block, adjusted for halo\012\012			float north, south, east, west, center, local_pwr;\012\012			// read input values from memory\012			// avoid out-of-bound indexes in x direction\012			if (real_x >= 0 && real_x < grid_cols)				// avoid out-of-bound indexes in x direction\012			{\012				// nothing to read on last row\012				if (comp_offset_y != grid_rows - 1)\012				{\012					in_sr[IN_SR_BASE + i]  = src[read_offset + i];// read new temperature values from memory\012				}\012\012				// there is nothing to compute on the first out-of-bound row\012				if (comp_offset_y >= 0)\012				{\012					local_pwr = pwr_in[comp_offset + i];	// read new power values from memory\012				}\012			}\012			\012			center = in_sr[SR_OFF_C + i];						// center index\012\012			// west neighbor\012			if (real_x == 0)								// leftmost column in matrix\012			{\012				west = center;								// fall back on center\012			}\012			else\012			{\012				west = in_sr[SR_OFF_W + i];					// read from shift register\012			}\012\012			// east neighbor\012			if (real_x == grid_cols - 1)						// rightmost column in matrix\012			{\012				east = center;								// fall back on center\012			}\012			else\012			{\012				east = in_sr[SR_OFF_E + i];					// read from shift register\012			}\012\012			// top neighbor\012			if (comp_offset_y == grid_rows - 1)				// topmost row, deliberately written like this to align with v1 kernel\012			{\012				north = center;							// fall back on center\012			}\012			else\012			{\012				north = in_sr[SR_OFF_N + i];					// read from shift register\012			}\012\012			// bottom neighbor\012			if (comp_offset_y == 0)							// bottommost row, deliberately written like this to align with v1 kernel\012			{\012				south = center;							// fall back on center\012			}\012			else\012			{\012				south = in_sr[SR_OFF_S + i];					// read from shift register\012			}\012\012			float v = local_pwr                              +\012					(north + south - 2.0f * center) * Ry_1 +\012					(east  + west  - 2.0f * center) * Rx_1 +\012					(AMB_TEMP - center)             * Rz_1;\012\012			float delta = sdc * v;\012\012			// write output values to memory\012			// the following condition is to avoid halos and going out of bounds in all axes\012			if (comp_offset_y >= 0 && real_block_x >= 0 && real_block_x < BSIZE - 2 && real_x < grid_cols)\012			{\012				dst[comp_offset + i] = center + delta;\012			}\012\012			//if (ID == 1 && comp_offset_y >=0 && comp_offset_y < grid_rows && real_x >=0 && real_x < grid_cols)\012				//printf(\"ID: %d, row: %04d, col: %04d, center: %f, top: %f, bottom: %f, left: %f, right: %f, pow: %f, out: %f\\n\", ID, comp_offset_y, real_x, center, north, south, west, east, local_pwr, out.data[i]);\012		}\012\012		// equivalent to x = (x + SSIZE) % BSIZE\012		x = (x + SSIZE) & (BSIZE - 1);						// move one chunk forward and reset to zero if end of row was reached\012\012		if (x == 0)										// if one row finished\012		{\012			if (y == grid_rows)								// if on last row (compute traverses one more plane than memory read/write)\012			{\012				y = 0;									// reset row number\012				bx += BSIZE - 2;							// go to next block, account for overlapping\012			}\012			else\012			{\012				y++;										// go to next row\012			}\012		}\012	}\012}\012"}];
